// Helper function to interpolate between two points
static inline void interpolate_vertex(float isovalue,
                                    float v1, float v2,
                                    float x1, float y1, float z1,
                                    float x2, float y2, float z2,
                                    float* out_x, float* out_y, float* out_z) {
    if (fabs(isovalue - v1) < 0.00001f) {
        *out_x = x1;
        *out_y = y1;
        *out_z = z1;
        return;
    }
    if (fabs(isovalue - v2) < 0.00001f) {
        *out_x = x2;
        *out_y = y2;
        *out_z = z2;
        return;
    }
    if (fabs(v1 - v2) < 0.00001f) {
        *out_x = x1;
        *out_y = y1;
        *out_z = z1;
        return;
    }

    float mu = (isovalue - v1) / (v2 - v1);
    *out_x = x1 + mu * (x2 - x1);
    *out_y = y1 + mu * (y2 - y1);
    *out_z = z1 + mu * (z2 - z1);
}

static inline float get_value(const float* values, int x, int y, int z,
                            int dimx, int dimy, int dimz) {
    return values[z * (dimx * dimy) + y * dimx + x];
}

static inline void process_cube(const float* values,
                        int x, int y, int z,
                        int dimx, int dimy, int dimz,
                        float isovalue,
                        float* vertices,
                        int* indices,
                        int* vertex_count,
                        int* index_count) {

    float cube_values[8];
    cube_values[0] = get_value(values, x, y, z, dimx, dimy, dimz);
    cube_values[1] = get_value(values, x + 1, y, z, dimx, dimy, dimz);
    cube_values[2] = get_value(values, x + 1, y + 1, z, dimx, dimy, dimz);
    cube_values[3] = get_value(values, x, y + 1, z, dimx, dimy, dimz);
    cube_values[4] = get_value(values, x, y, z + 1, dimx, dimy, dimz);
    cube_values[5] = get_value(values, x + 1, y, z + 1, dimx, dimy, dimz);
    cube_values[6] = get_value(values, x + 1, y + 1, z + 1, dimx, dimy, dimz);
    cube_values[7] = get_value(values, x, y + 1, z + 1, dimx, dimy, dimz);

    int cubeindex = 0;
    for (int i = 0; i < 8; i++) {
        if (cube_values[i] < isovalue)
            cubeindex |= (1 << i);
    }

    if (edgeTable[cubeindex] == 0)
        return;

    float edge_verts[12][3];  // [x,y,z] for each possible edge vertex

    if (edgeTable[cubeindex] & 1)
        interpolate_vertex(isovalue, cube_values[0], cube_values[1],
                         x, y, z,             // vertex 0
                         x + 1, y, z,         // vertex 1
                         &edge_verts[0][0], &edge_verts[0][1], &edge_verts[0][2]);

    if (edgeTable[cubeindex] & 2)
        interpolate_vertex(isovalue, cube_values[1], cube_values[2],
                         x + 1, y, z,         // vertex 1
                         x + 1, y + 1, z,     // vertex 2
                         &edge_verts[1][0], &edge_verts[1][1], &edge_verts[1][2]);

    if (edgeTable[cubeindex] & 4)
        interpolate_vertex(isovalue, cube_values[2], cube_values[3],
                         x + 1, y + 1, z,     // vertex 2
                         x, y + 1, z,         // vertex 3
                         &edge_verts[2][0], &edge_verts[2][1], &edge_verts[2][2]);

    if (edgeTable[cubeindex] & 8)
        interpolate_vertex(isovalue, cube_values[3], cube_values[0],
                         x, y + 1, z,         // vertex 3
                         x, y, z,             // vertex 0
                         &edge_verts[3][0], &edge_verts[3][1], &edge_verts[3][2]);

    if (edgeTable[cubeindex] & 16)
        interpolate_vertex(isovalue, cube_values[4], cube_values[5],
                         x, y, z + 1,         // vertex 4
                         x + 1, y, z + 1,     // vertex 5
                         &edge_verts[4][0], &edge_verts[4][1], &edge_verts[4][2]);

    if (edgeTable[cubeindex] & 32)
        interpolate_vertex(isovalue, cube_values[5], cube_values[6],
                         x + 1, y, z + 1,     // vertex 5
                         x + 1, y + 1, z + 1, // vertex 6
                         &edge_verts[5][0], &edge_verts[5][1], &edge_verts[5][2]);

    if (edgeTable[cubeindex] & 64)
        interpolate_vertex(isovalue, cube_values[6], cube_values[7],
                         x + 1, y + 1, z + 1, // vertex 6
                         x, y + 1, z + 1,     // vertex 7
                         &edge_verts[6][0], &edge_verts[6][1], &edge_verts[6][2]);

    if (edgeTable[cubeindex] & 128)
        interpolate_vertex(isovalue, cube_values[7], cube_values[4],
                         x, y + 1, z + 1,     // vertex 7
                         x, y, z + 1,         // vertex 4
                         &edge_verts[7][0], &edge_verts[7][1], &edge_verts[7][2]);

    if (edgeTable[cubeindex] & 256)
        interpolate_vertex(isovalue, cube_values[0], cube_values[4],
                         x, y, z,             // vertex 0
                         x, y, z + 1,         // vertex 4
                         &edge_verts[8][0], &edge_verts[8][1], &edge_verts[8][2]);

    if (edgeTable[cubeindex] & 512)
        interpolate_vertex(isovalue, cube_values[1], cube_values[5],
                         x + 1, y, z,         // vertex 1
                         x + 1, y, z + 1,     // vertex 5
                         &edge_verts[9][0], &edge_verts[9][1], &edge_verts[9][2]);

    if (edgeTable[cubeindex] & 1024)
        interpolate_vertex(isovalue, cube_values[2], cube_values[6],
                         x + 1, y + 1, z,     // vertex 2
                         x + 1, y + 1, z + 1, // vertex 6
                         &edge_verts[10][0], &edge_verts[10][1], &edge_verts[10][2]);

    if (edgeTable[cubeindex] & 2048)
        interpolate_vertex(isovalue, cube_values[3], cube_values[7],
                         x, y + 1, z,         // vertex 3
                         x, y + 1, z + 1,     // vertex 7
                         &edge_verts[11][0], &edge_verts[11][1], &edge_verts[11][2]);

    for (int i = 0; triTable[cubeindex][i] != -1; i += 3) {
        for (int j = 0; j < 3; j++) {
            int edge = triTable[cubeindex][i + j];
            vertices[*vertex_count * 3] = edge_verts[edge][0];
            vertices[*vertex_count * 3 + 1] = edge_verts[edge][1];
            vertices[*vertex_count * 3 + 2] = edge_verts[edge][2];

            indices[*index_count] = *vertex_count;

            (*vertex_count)++;
            (*index_count)++;
        }
    }
}

// Main marching cubes function
static inline int march_cubes(const float* values,      // Input 3D array of values
                int dimz, int dimy, int dimx,  // Dimensions of the input array
                float isovalue,            // Isovalue to extract surface at
                float** out_vertices,      // Output vertex array [x,y,z,x,y,z,...]
                int** out_indices,         // Output index array
                int* out_vertex_count,     // Number of vertices
                int* out_index_count) {    // Number of indices

    // Calculate maximum possible number of triangles
    int max_triangles = (dimx - 1) * (dimy - 1) * (dimz - 1) * 5;

    // Allocate output arrays
    float* vertices = malloc(sizeof(float) * max_triangles * 3 * 3); // 3 vertices per tri, 3 coords per vertex
    int* indices = malloc(sizeof(int) * max_triangles * 3);          // 3 indices per triangle

    if (!vertices || !indices) {
        free(vertices);
        free(indices);
        return 0;
    }

    int vertex_count = 0;
    int index_count = 0;

    // Process each cube in the grid
    for (int z = 0; z < dimz - 1; z++) {
        for (int y = 0; y < dimy - 1; y++) {
            for (int x = 0; x < dimx - 1; x++) {
                process_cube(values, x, y, z, dimx, dimy, dimz,
                           isovalue, vertices, indices,
                           &vertex_count, &index_count);
            }
        }
    }

    // Shrink arrays to actual size
    vertices = realloc(vertices, sizeof(float) * vertex_count * 3);
    indices = realloc(indices, sizeof(int) * index_count);

    *out_vertices = vertices;
    *out_indices = indices;
    *out_vertex_count = vertex_count;
    *out_index_count = index_count;

    return 0;
}
